pkg load imagefolder1 = '/Documents/Octave/RGB_cropped';folder2 = '/Documents/Octave/IR_resized';folderout = '/Documents/Octave/fused';outfilepattern = 'DJI_fused%03d.tiff';   %see the documentation of sprintf for format string syntaxfilelist1 = dir(fullfile(folder1, '*.tiff'));filelist2 = dir(fullfile(folder2, '*.tiff'));assert(numel(filelist1) == numel(filelist2), 'Mismatch between file count in input folders.');for fidx = 1:numel(filelist1)    rgb = im2uint8 (imread (fullfile(folder1, filelist1(fidx).name)));    numrows = size(rgb,1);    numrows = size(rgb,1);    numcols = size(rgb,2);    ir = im2uint8 (imread(fullfile(folder2, filelist2(fidx).name)));            t = fullfile(folderout, sprintf(outfilepattern, fidx));            imwrite (rgb, t, "alpha", ir)end