pkg load imagefolder1 = '/Documents/Octave/IR';folderout = '/Documents/Octave/IR_resized';outfilepattern = 'DJI_resized%03d.tiff';   %see the documentation of sprintf for format string syntaxfilelist1 = dir(fullfile(folder1, '*.JPG'));for ii = 1:numel (filelist1)   # loop over jpg files  img = imread(fullfile(folder1, filelist1(ii).name));  imgray = rgb2gray(img);  results = imresize(imgray, 6);  t = fullfile(folderout, sprintf(outfilepattern, ii));    imwrite (results,t)end