pkg load imagefolder1 = '/Documents/Octave/RGB';folderout = '/Documents/Octave/RGB_cropped';outfilepattern = 'DJI_cropped%03d.tiff';   %see the documentation of sprintf for format string syntaxfilelist1 = dir(fullfile(folder1, '*.JPG'));for ii = 1:numel (filelist1)   # loop over jpg files  img = imread(fullfile(folder1, filelist1(ii).name));  results= imcrop (img, [104 77 3839 2879]);    t = fullfile(folderout, sprintf(outfilepattern, ii));  imwrite (results,t)end